	{"brief":"Will determine if the Anti ScreenCapture is currently providing protection","friendly_name":"GetAntiScreenCaptureState","id":120012,"name":"WAAPI_MID_GET_ANTI_SCREENCAPTURE_STATE","out_params":[{"desc":"True if screen capture protection is enabled.","name":"enabled","optional":false,"type":"boolean"}],"override_default_in":true,"show_in_documentation":true},
	{"brief":"Will enable/disable/allow/deny the Anti ScreenCapture processes","friendly_name":"ManageAntiScreenCaptureProcess","id":120013,"in_params":[{"desc":"The operation to be done. Should be 'enable', 'disable', 'allow' or 'deny'.","name":"operation","optional":false,"type":"string","values":["enable","disable","allow","deny"]},{"desc":"A process id used to identify the process. It also can be an array for multi-processes or empty for all processes","name":"process_id","optional":true,"type":"number"},{"desc":"If true, will block Copy-Paste operations on the machine. Applies only in case of 'enable' operation. Default value is false.","name":"protect_copy_paste","optional":true,"type":"boolean"}],"name":"WAAPI_MID_MANAGE_ANTI_SCREENCAPTURE_PROCESS","override_default_in":true,"show_in_documentation":true}
